(function($){$.fn.animulnav=function(){var div=this,ul=this.find('ul'),ulPadding=15;var divWidth=this.width();var lastLi=ul.find('li:last-child');div.mousemove(function(e){var ulWidth=lastLi[0].offsetLeft+lastLi.outerWidth()+ulPadding;var left=(e.pageX-div.offset().left)*(ulWidth-divWidth)/divWidth;div.scrollLeft(left)})}})(jQuery);